package tnsfif.day2;

public class NestedIfElseDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10, b=20, c=32;
		
		System.out.println("the largest no is:");
		if(a>b)
		{
			if(a>c) {
				System.out.println(a);
			}
			else
			{
				System.out.println(c);
			}
		}

			

	}

}
